import { Card, Image, Label, Icon } from 'semantic-ui-react';
import './style.css';
import axios from 'axios';

export default class CardExampleImageCard extends Component{
constructor(props) {
  super(props);
  // const { title, link, img, price } = props;

  this.state={
    favorite: false,
  };

    function saveFavorite(e) {
    e.preventDefault();
    axios.post("/api/favorite", { ...props })
    .then((res) => {
      console.log('favorite saved', res.data);
      this.setState({
        favorite: true,
      })
      return(
        <Label id="label">
          <Icon name='heart' color='pink' />
        </Label>
      )})
    .catch((err) => console.log('there was an error: ', err));
  }

    return (
      <div id="cards">
        <Card 
          id="each-card" 
          >
          <Label id="label"
            onClick={saveFavorite}>
            <Icon name='heart' />
          </Label>
