import React, {Component} from 'react'
import {Button, Icon, Input } from 'semantic-ui-react'
import axios from 'axios'
const cheerio= require("cheerio");


export default class SearchBar extends Component {

    constructor(props) {
        super(props);
        this.state = {
          query: '',
        };
      }

      queryChange = (e) => {
        const { value } = e.target;
        this.setState({
          query: value
        });
      }

    search = (e) => {
        e.preventDefault();
        console.log('query is', this.state.query)
        const { query } = this.state;
        axios.get("/scrape", function (req, res){
            axios.all([
                axios.get('https://www.sephora.com/search?keyword='+query),
                axios.get('https://www.peachandlily.com/search?q='+query),
                axios.get('https://sokoglam.com/search?type=product&q='+query),
            ]).then(axios.spread((sephRes, plRes, sgRes) => {
                var $ = cheerio.load(sephRes.data + plRes.data + sgRes.data);
                
                console.log(sephRes.data);
                // console.log(plRes.data);
                // console.log(sgRes.data);
                }));
            });

    }



    render(){
        console.log('the state', this.state);
            return(
            <Input  
            iconPosition='left'
            style={{width:"600px"}} 
            onChange={this.queryChange}
            action={{ 
                color: 'pink', 
                icon: 'search',
                onClick : this.search
             }}
                />
        )
    }
}

