                    }
                 })
            .catch(function (error) {
                console.log(error);
            });
    };


    render() {
        console.log('the state', this.state);
        return (
            <Input
                iconPosition='left'
                style={{ width: "600px" }}
                onChange={this.queryChange}
                action={{
                    color: 'pink',
                    icon: 'search',
                    onClick: this.search
                }}
            />

        )
    }
}
