import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import {Button} from 'semantic-ui-react';


export default class Logged extends Component {
    constructor(props) {
        super(props);
        this.state = {
            loggedIn: false,
        };

    }

 displayLink =(e) => {
     e.preventDefault();
    if (!localStorage.getItem('email')) {
        return (
            <Link to={"/"}>Login</Link>
        )
    } else {
        this.setState({
            loggedIn: true,
        })
        console.log('the state on the logged component is', this.state)
        return (
            <Link to={'/user'}><span id="user-email">{localStorage.getItem('email')}</span></Link>
        )
    }
}

    render(){
    return(
        <Button basic color='purple'
        onLoad={'displayLink'}>
      </Button>
 )
    }

};
